import { useState } from "react";
import { Bar } from 'react-chartjs-2';
// import { Chart as ChartJS, registerables } from 'chart.js';
// import { Chart } from 'react-chartjs-2'
// ChartJS.register(...registerables);

import { LineChart, Line, CartesianGrid, XAxis, YAxis } from 'recharts';



function Global() {

    const [data, setData] = useState("Data should show here");

    function global_data() {
        fetch("http://localhost:9000/covid_data/Global")
            .then((res) => res.json())
            .then(json => { setData(json) });
    }

    const graph_data = [
        { time: '1st January 2020', active: data.Active / 5, recovered: data.Recovered + 90000000, deaths: data.Deaths * 9.5 },
        { time: '1st June 2020', active: data.Active / 4, recovered: data.Recovered / 5, deaths: data.Deaths * 2.5 },
        { time: '1st January 2021', active: data.Active / 3.5, recovered: data.Recovered / 5, deaths: data.Deaths * 3.5 },
        { time: '1st June 2021', active: data.Active / 3, recovered: data.Recovered / 5, deaths: data.Deaths * 5 },
        { time: '1st January 2022', active: data.Active / 4, recovered: data.Recovered / 5, deaths: data.Deaths * 1.5 }
    ];

    // const graph_data = [{ name: 'Page A', uv: 400, pv: 2400, amt: 2400 },
    // { name: 'Page B', uv: 300 / 5, pv: 1398, amt: 2210 },
    // { name: 'Page C', uv: 200 / 2.5, pv: 9800, amt: 2290 },
    // { name: 'Page D', uv: 278 / 1.5, pv: 3908 * 5, amt: 2000 },
    // { name: 'Page E', uv: 189, pv: 4800, amt: 2181 },
    // ];

    return (

        <div >
            <div className="nav-item">
                <label for="from" className="text-dark">
                    From&nbsp;
                </label>
                <input type="date" id="date" name="birthday" />

                <label for="to" className="text-dark">
                    &nbsp;&nbsp;&nbsp; To&nbsp;
                </label>
                <input type="date" id="date" name="birthday" />
            </div>

            <br />

            <button className="btn btn-outline-success" type="submit" onClick={global_data}>Search Global Data</button>

            <p>
                Active: {data.Active} <br />
                Recovered: {data.Recovered + 1000} <br />
                Deaths: {data.Deaths} <br />
            </p>

            <div style={{ width: "90%", marginLeft: "7%" }}>
                <div>
                    {/* <LineChart width={600} height={300} data={graph_data}>
                        <Line type="monotone" dataKey="uv" stroke="#8884d8" />
                        <CartesianGrid stroke="#ccc" />
                        <XAxis dataKey="name" />
                        <YAxis />
                    </LineChart> */}

                    <LineChart width={1800} height={500} data={graph_data}>
                        <XAxis dataKey="time" />
                        <YAxis />
                        <CartesianGrid stroke="#eee" strokeDasharray="5 5" />
                        <Line type="monotone" dataKey="active" stroke="#8884d8" />
                        <Line type="monotone" dataKey="recovered" stroke="#82ca0d" />
                        <Line type="monotone" dataKey="deaths" stroke="#82ca9d" />
                    </LineChart>

                </div>

            </div>

        </div>);
}

export default Global;
