import { useState } from "react";
import { Bar } from 'react-chartjs-2';
// import { Chart as ChartJS, registerables } from 'chart.js';
// import { Chart } from 'react-chartjs-2'
// ChartJS.register(...registerables);

import { LineChart, Line, CartesianGrid, XAxis, YAxis, Tooltip, Legend } from 'recharts';



function Global() {

    const [data, setData] = useState("Data should show here");

    function global_data() {
        fetch("http://localhost:9000/covid_data/Global")
            .then((res) => res.json())
            .then(json => { setData(json) });
    }

    function data_calc() {
        // TODO: change graph based on selected date range
        let from = document.getElementsByClassName("fromDate").value;
        let to = document.getElementsByClassName("toDate").value;
        console.log("FROM " + from);
        console.log("TO " + to);
    }

    const graph_data = [
        { time: '1st January 2020', Active: Number(data.Active / 5), Recovered: data.Recovered + 9000000, Deaths: data.Deaths * 9 },
        { time: '1st June 2020', Active: data.Active / 4, Recovered: data.Recovered + 90000000, Deaths: data.Deaths * 2 },
        { time: '1st January 2021', Active: data.Active / 3, Recovered: data.Recovered + 90000000 / 3, Deaths: data.Deaths * 3 },
        { time: '1st June 2021', Active: data.Active / 3, Recovered: data.Recovered + 90000000 / 1.5, Deaths: data.Deaths * 5 },
        { time: '1st January 2022', Active: data.Active / 4, Recovered: data.Recovered + 90000000 / 2.5, Deaths: data.Deaths * 1 }
    ];

    // const graph_data = [{ name: 'Page A', uv: 400, pv: 2400, amt: 2400 },
    // { name: 'Page B', uv: 300 / 5, pv: 1398, amt: 2210 },
    // { name: 'Page C', uv: 200 / 2.5, pv: 9800, amt: 2290 },
    // { name: 'Page D', uv: 278 / 1.5, pv: 3908 * 5, amt: 2000 },
    // { name: 'Page E', uv: 189, pv: 4800, amt: 2181 },
    // ];

    return (

        <div >
            <form className="nav-item">
                <label for="from" className="text-dark">
                    From&nbsp;
                </label>
                <input type="date" id="date" className="fromDate" min="2020-01-01" max="2022-02-26" value="2020-01-01" />

                <label for="to" className="text-dark">
                    &nbsp;&nbsp;&nbsp; To&nbsp;
                </label>
                <input type="date" id="date" className="toDate" min="2020-01-02" max="2022-02-25" value="2022-02-25" />


            </form>

            <br />

            <button className="btn btn-outline-success" type="submit" onClick={() => { global_data(); data_calc() }}>
                Search Global Data
            </button>


            {/* <p>
                Active: {data.Active} <br />
                Recovered: {data.Recovered + 1000} <br />
                Deaths: {data.Deaths} <br />
            </p> */}

            <div style={{ width: "90%", margin: "7%" }}>

                <LineChart width={1800} height={500} data={graph_data}>
                    <XAxis dataKey="time" />
                    <YAxis />
                    <Tooltip />
                    <Legend />
                    <CartesianGrid stroke="#eee" strokeDasharray="5 5 5" />
                    <Line type="monotone" dataKey="Active" stroke="#8884d8" />
                    <Line type="monotone" dataKey="Recovered" stroke="#242936" />
                    <Line type="monotone" dataKey="Deaths" stroke="#82ca9d" />
                </LineChart>

            </div>

        </div>);
}

export default Global;
