import { useState } from "react";
import { Bar } from 'react-chartjs-2';
import { Chart as ChartJS, registerables } from 'chart.js';
// import { Chart } from 'react-chartjs-2'
ChartJS.register(...registerables);

function Global() {

    const [data, setData] = useState("Data should show here");

    function global_data() {
        fetch("http://localhost:9000/covid_data/Global")
            .then((res) => res.json())
            .then(json => { setData(json) });
    }

    const graphState = {
        labels: ['Active', 'Recovered', 'Deceased'],
        datasets: [
            {
                label: 'Covid',
                backgroundColor: [
                    '#B21F00',
                    '#C9DE00',
                    '#2FDE00',
                    // '#00A6B4',
                    // '#6800B4'
                ],
                hoverBackgroundColor: [
                    '#501800',
                    '#4B5000',
                    '#175000',
                    // '#003350',
                    // '#35014F'
                ],
                data: [data.Active, data.Recovered, data.Deaths]
            }
        ]
    }

    const state = {
        labels: ['Active', 'Recovered', 'Deceased'],
        datasets: [
            {
                label: 'Covid',
                backgroundColor: 'rgba(75,192,192,1)',
                borderColor: 'rgba(0,0,0,1)',
                borderWidth: 2,
                data: [data.Active, data.Recovered, data.Deaths]
            }
        ]
    }


    return (

        <div >
            <div className="nav-item">
                <label for="from" className="text-dark">
                    From&nbsp;
                </label>
                <input type="date" id="date" name="birthday" />

                <label for="to" className="text-dark">
                    &nbsp;&nbsp;&nbsp; To&nbsp;
                </label>
                <input type="date" id="date" name="birthday" />
            </div>

            <br />

            <button className="btn btn-outline-success" type="submit" onClick={global_data}>Search Global Data</button>

            <p>
                Active: {data.Active} <br />
                Recovered: {data.Recovered} <br />
                Deaths: {data.Deaths} <br />
            </p>


            <div style={{ width: "800px", marginLeft: "30%" }}>
                {/* <Pie
                    data={graphState}
                    options={{
                        title: {
                            display: true,
                            text: 'Average Rainfall per month',
                            fontSize: 20
                        },
                        legend: {
                            display: true,
                            position: 'right'
                        }
                    }}
                /> */}

                {/* <Doughnut
                    data={graphState}
                    options={{
                        title: {
                            display: true,
                            text: 'Covid Global data',
                            fontSize: 50
                        },
                        legend: {
                            display: true,
                            position: 'center'
                        }
                    }}
                /> */}

                <div>
                    <Bar
                        data={state}
                        options={{
                            title: {
                                display: true,
                                text: 'Average Rainfall per month',
                                fontSize: 20
                            },
                            legend: {
                                display: false,
                                position: 'right'
                            }
                        }}
                    />
                </div>

            </div>


        </div>);
}

export default Global;
