import { useRef, useState } from "react";
// import React from "react";
import { LineChart, Line, CartesianGrid, XAxis, YAxis, Tooltip, Legend } from 'recharts';

function Global() {
    const [data, setData] = useState("Data should show here");

    function global_data() {
        fetch("http://localhost:9000/covid_data/Global")
            .then((res) => res.json())
            .then(json => { setData(json) });
        // console.log(data.Active);
    }

    const [active, setActive] = useState([
        // 352561230,
        parseInt((data.Active) / 5),
        parseInt(data.Active / 4),
        parseInt(data.Active / 3),
        parseInt(data.Active / 3),
        parseInt(data.Active / 4)
    ]);

    const [recovered, setRecover] = useState([
        // 35233561230,
        parseInt(data.Recovered + 9000000),
        parseInt(data.Recovered + 90000000),
        parseInt(data.Recovered + 90000000 / 3),
        parseInt(data.Recovered + 90000000 / 1.5),
        parseInt(data.Recovered + 90000000 / 2.5)
    ]);
    const [deaths, setDeath] = useState([
        // 35236451230,
        parseInt(data.Deaths * 9),
        parseInt(data.Deaths * 2),
        parseInt(data.Deaths * 3),
        parseInt(data.Deaths * 5),
        parseInt(data.Deaths * 1)
    ]);

    const [graph_data, setGraph] = useState([
        { time: '1st January 2020', Active: active[0], Recovered: recovered[0], Deaths: deaths[0] },
        { time: '1st June 2020', Active: active[1], Recovered: recovered[1], Deaths: deaths[1] },
        { time: '1st January 2021', Active: active[2], Recovered: recovered[2], Deaths: deaths[2] },
        { time: '1st June 2021', Active: active[3], Recovered: recovered[3], Deaths: deaths[3] },
        { time: '1st January 2022', Active: active[4], Recovered: recovered[4], Deaths: deaths[4] }
    ]);

    let from = useRef(null);
    let to = useRef(null);

    function data_calc() {
        // TODO: change graph based on selected date range
        // console.log(from.current.value);
        let fromDate = from.current.value.split("-");
        let toDate = to.current.value.split("-");
        // console.log(active[0], active[1], active[2], active[3], active[4]);

        if (from.current.value === "" || to.current.value === "") {
            alert("Please enter Dates");
        }
        else if (parseInt(toDate[0]) < parseInt(fromDate[0])) {
            alert("Invalid date range");
        }
        else {
            let toDateCalc = toDate[1] + 10;
            let fromDateCalc = fromDate[1] + 10;
            setActive([
                Math.abs(parseInt((data.Active) / (toDateCalc - 5))),
                Math.abs(parseInt((data.Active) / (fromDateCalc - 2))),
                Math.abs(parseInt((data.Active) / (toDate[1] - 6))),
                Math.abs(parseInt((data.Active) / (fromDate[1] - 1))),
                Math.abs(parseInt((data.Active) / (toDate[1] - 8)))
            ]);

            setRecover([
                Math.abs(parseInt(data.Recovered + 9000000)),
                Math.abs(parseInt(data.Recovered + 90000000)),
                Math.abs(parseInt(data.Recovered + 90000000 / 3)),
                Math.abs(parseInt(data.Recovered + 90000000 / 1.5)),
                Math.abs(parseInt(data.Recovered + 90000000 / 2.5))
            ]);

            setDeath([
                Math.abs(parseInt(data.Deaths * 9)),
                Math.abs(parseInt(data.Deaths * 2)),
                Math.abs(parseInt(data.Deaths * 3)),
                Math.abs(parseInt(data.Deaths * 5)),
                Math.abs(parseInt(data.Deaths * 1))
            ]);
            console.log(Math.abs(parseInt((data.Active) / (fromDate[1] - 3))));
            setGraph(
                [
                    { time: '1st January 2020', Active: active[0], Recovered: recovered[0], Deaths: deaths[0] },
                    { time: '1st June 2020', Active: active[1], Recovered: recovered[1], Deaths: deaths[1] },
                    { time: '1st January 2021', Active: active[2], Recovered: recovered[2], Deaths: deaths[2] },
                    { time: '1st June 2021', Active: active[3], Recovered: recovered[3], Deaths: deaths[3] },
                    { time: '1st January 2022', Active: active[4], Recovered: recovered[4], Deaths: deaths[4] }
                ]
            );
        }

    }

    return (

        <div >
            <form className="nav-item">
                <label for="from" className="text-dark">
                    From&nbsp;
                </label>

                <input type="date" id="date" className="fromDate" ref={from} min="2020-01-01" max="2022-02-26" placeholder="2020-01-01" />

                <label for="to" className="text-dark">
                    &nbsp;&nbsp;&nbsp; To&nbsp;
                </label>

                <input type="date" id="date" className="toDate" ref={to} min="2020-01-02" max="2022-02-25" placeholder="2022-02-25" />

                <br /><br />

                <input className="btn btn-outline-success" type="button" value="Search Global Data" onClick={() => { global_data(); data_calc() }} />

            </form>

            <p>
                Please select the dates to searc for global Data
            </p>

            <div style={{ margin: "7%", }}>

                <LineChart width={1800} height={500} data={graph_data} >
                    <CartesianGrid stroke="#eee" strokeDasharray="5 5 5" />
                    <XAxis dataKey="time" />
                    {/* <YAxis type="number" domain={[dataMin => (0 - Math.abs(dataMin)),
                    dataMax => (dataMax * 1.1)]} /> */}
                    <YAxis />

                    <Tooltip />
                    <Legend />
                    <Line type="monotone" dataKey="Active" stroke="#8884d8" />
                    <Line type="monotone" dataKey="Recovered" stroke="#242936" />
                    <Line type="monotone" dataKey="Deaths" stroke="#82ca9d" />
                </LineChart>

            </div>

        </div >);
}

export default Global;
