import { useState } from "react";

const Table = (props) => {

    const [response, setResponse] = useState(null);

    const [countries, setCountries] = useState([
        // "Armenia",
        // "Australia",
        // "Austria",
        // "Azerbaijan",
        // "Bahamas",
        // "Bangladesh",
        // "Belarus",
        // "Belgium",
        // "Bolivia",
        // "Bosnia_and_Herzegovina",
        // "Brazil",
        // "Bulgaria",
        // "Cambodia",
        // "Canada",
        // "Chile",
        // "China",
        // "Colombia",
        // "Costa_Rica",
        // "Croatia",
        // "Cuba",
        // "Cyprus",
        // "Czechia",
        // "Denmark",
        // "Dominican_Republic",
        // "Ecuador",
        // "Egypt",
        // "El_Salvador",
        // "Estonia",
        // "Finland",
        // "France",
        // "Georgia",
        // "Germany",
        // "Ghana",
        // "Greece",
        // "Guatemala",
        // "Honduras",

    ]);

    function global_data() {
        fetch("http://localhost:9000/covid_data/Global")
            .then(res => res.text())
            .then(data => {
                setResponse(data);
                for (let i = 0; i < data.length; i++) {
                    countries.push({
                        "Country": data[i]["Country"],
                        "Cases": data[i]["TotalConfirmed"],
                        "Deaths": data[i]["Deaths"],
                        "Recovered": data[i]["Recovered"],
                        "Date": data[i]["Date"]
                    });
                }

                console.log(countries);

            });


    }

    const [selectedCountry, setSelectedCountry] = useState("Select Location");

    return (
        <div className="container" onLoad={global_data()} style={{
            margin: "5rem", textAlign: "center",
        }}>
            <div className="row">
                <div className="col">
                    <div className="dropdown" >
                        <a
                            className="btn btn-secondary dropdown-toggle"
                            href="#"
                            role="button"
                            id="dropdownMenuLink"
                            data-bs-toggle="dropdown"
                            aria-expanded="false"
                        >
                            {selectedCountry}
                        </a>

                        <ul className="dropdown-menu" aria-labelledby="dropdownMenuLink"
                            style={{ overflow: 'scroll', height: '200px', 'overflowY': 'visible', 'overflowX': 'hidden' }}
                        >
                            {countries.map((country, index) => (
                                <li
                                    className="dropdown-item"
                                    onClick={(e) => {
                                        setSelectedCountry(e.target.innerText);
                                        // console.log(e.target.innerText);
                                    }}>
                                    {country}
                                </li>
                            ))}
                        </ul>
                    </div>
                </div>

                <div className="col">
                    <div className="card" style={{ width: "18rem" }}>
                        <div className="card-body">
                            <h5 className="card-title">Active</h5>
                            <h6 className="card-subtitle mb-2 text-muted">595,856</h6>

                        </div>
                    </div>
                </div>

                <div className="col">
                    <div className="card" style={{ width: "18rem" }}>
                        <div className="card-body">
                            <h5 className="card-title">Recovered</h5>
                            <h6 className="card-subtitle mb-2 text-muted">--NaN--</h6>

                        </div>
                    </div>
                </div>

                <div className="col">
                    <div className="card" style={{ width: "18rem" }}>
                        <div className="card-body">
                            <h5 className="card-title">Deceased</h5>
                            <h6 className="card-subtitle mb-2 text-muted">--NaN--</h6>

                        </div>
                    </div>
                </div>

            </div >

        </div >
    );
};

export default Table;
